INSERT INTO `student_tests` (`id`, `student_id`, `topic_id`, `difficulty`, `question_count`, `content`, `score`, `completed_at`, `created_at`) VALUES (9, 9, 9, 'hard', 10, '{\"title\":\"\\u0418\\u043d\\u0442\\u0435\\u0433\\u0440\\u0430\\u043b Test\",\"instructions\":\"Choose the correct answer from the provided options for each question. You have 20 minutes to complete the test.\",\"totalPoints\":100,\"estimatedTime\":\"20 minutes\",\"questions\":[{\"id\":1,\"type\":\"multiple_choice\",\"question\":\"What is the integral of sec(x)?\",\"options\":[\"tan(x) + C\",\"sec(x) tan(x) + C\",\"cot(x) + C\",\"csc(x) + C\"],\"correctAnswer\":1,\"points\":10},{\"id\":2,\"type\":\"multiple_choice\",\"question\":\"What is the integral of 1\\/(x^2+a^2)?\",\"options\":[\"arctan(x\\/a) \\/ a + C\",\"tan(x\\/a) \\/ a + C\",\"cot(x\\/a) \\/ a + C\",\"arccot(x\\/a) \\/ a + C\"],\"correctAnswer\":0,\"points\":10},{\"id\":3,\"type\":\"multiple_choice\",\"question\":\"What is the integral of cos^2(x)?\",\"options\":[\"sin(x) + C\",\"(x\\/2) + (sin(2x)\\/4) + C\",\"(x\\/2) - (sin(2x)\\/4) + C\",\"(x\\/2) + C\"],\"correctAnswer\":1,\"points\":10},{\"id\":4,\"type\":\"multiple_choice\",\"question\":\"What is the integral of e^(2x)?\",\"options\":[\"2e^(2x) + C\",\"e^(2x) + C\",\"(1\\/2)e^(2x) + C\",\"(2\\/3)e^(2x) + C\"],\"correctAnswer\":2,\"points\":10},{\"id\":5,\"type\":\"multiple_choice\",\"question\":\"What is the integral of ln(x)?\",\"options\":[\"xln(x) - x + C\",\"xln(x) + C\",\"xln(x) - 1 + C\",\"xln(x) - x - 1 + C\"],\"correctAnswer\":0,\"points\":10},{\"id\":6,\"type\":\"multiple_choice\",\"question\":\"What is the integral of sin(2x)?\",\"options\":[\"-cos(2x)\\/2 + C\",\"cos(2x)\\/2 + C\",\"-cos(x) + C\",\"cos(x) + C\"],\"correctAnswer\":0,\"points\":10},{\"id\":7,\"type\":\"multiple_choice\",\"question\":\"What is the integral of 1\\/(1-x^2)?\",\"options\":[\"arcsin(x) + C\",\"arccos(x) + C\",\"arctan(x) + C\",\"ln|x - 1| - ln|x + 1| + C\"],\"correctAnswer\":3,\"points\":10},